// src/Color.tsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BsCopy } from "react-icons/bs";

interface Color {
    id: number;
    name: string;
    hex_code: string;
    created_at: number;
}

const Color: React.FC = () => {
    const [colors, setColors] = useState<Color[]>([]);
    const [newColorName, setNewColorName] = useState('');
    const [newColorHex, setNewColorHex] = useState('');
    const [filterText, setFilterText] = useState<string>('');
    const [favoriteColors, setFavoriteColors] = useState<string[]>([]);




    useEffect(() => {
        fetchColors();
    }, []);

    const fetchColors = () => {
        axios.get('http://localhost:8000/api/colors')
            .then(response => {
                setColors(response.data);
            })
            .catch(error => {
                console.error('Error fetching colors:', error);
            });
    };

    const handleCreateColor = () => {
        axios.post('http://localhost:8000/api/colors', {
            name: newColorName,
            hex_code: newColorHex,
        })
            .then(() => {
                // Refresh colors after creation
                fetchColors();
                // Clear form fields
                setNewColorName('');
                setNewColorHex('');
            })
            .catch(error => {
                console.error('Error creating color:', error);
            });
    };

    const handleDeleteColor = (id: number) => {
        axios.delete(`http://localhost:8000/api/colors/${id}`)
            .then(() => {
                // Refresh colors after deletion
                fetchColors();
            })
            .catch(error => {
                console.error('Error deleting color:', error);
            });
    };

    const handleFilterChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { value } = e.target;
        setFilterText(value); // Update filter text state on input change
    };

    const filteredColors = colors.filter(color =>
        color.name.toLowerCase().includes(filterText.toLowerCase()) ||
        color.hex_code.toLowerCase().includes(filterText.toLowerCase())
    );

    const handleSetFavoriteColor = (hexCode: string) => {
        if (!favoriteColors.includes(hexCode)) {
            setFavoriteColors([...favoriteColors, hexCode]);
        }
    };

    const handleRemoveFavoriteColor = (hexCode: string) => {
        const updatedFavorites = favoriteColors.filter(color => color !== hexCode);
        setFavoriteColors(updatedFavorites);
    };
    const copyHexToClipboard = (hexValue: string) => {
        navigator.clipboard.writeText(hexValue)
            .then(() => {
                alert(`Copied the hex code: ${hexValue}`);
            })
            .catch(err => {
                console.error('Error copying hex code: ', err);
                alert("Unable to copy hex code. Please try again.");
            });
    };
    const sortColors = (field: keyof Color, order: 'asc' | 'desc') => {
        const sortedColors = [...colors].sort((a, b) => {
            const fieldValueA = typeof a[field] === 'string' ? a[field] as string : '';
            const fieldValueB = typeof b[field] === 'string' ? b[field] as string : '';

            if (order === 'asc') {
                return fieldValueA.localeCompare(fieldValueB);
            } else {
                return fieldValueB.localeCompare(fieldValueA);
            }
        });
        setColors(sortedColors);
    };


    return (
        <div className="">
            <h1>Colors</h1>
            <div className="container">
                <button onClick={() => sortColors('name', 'asc')}>Sort by Name (A-Z)</button>
                <button onClick={() => sortColors('name', 'desc')}>Sort by Name (Z-A)</button>
                <button onClick={() => sortColors('created_at', 'asc')}>Sort by Title (Oldest)</button>
                <button onClick={() => sortColors('created_at', 'desc')}>Sort by Title (Newest)</button>

            </div>
            <div>

                        <input type="text" className="px-4 py-2" placeholder="Search for color name..."
                               value={filterText}
                               onChange={handleFilterChange} />



                {/*<input*/}
                {/*    type="text"*/}
                {/*    placeholder="Filter by name or hex code"*/}
                {/*    value={filterText}*/}
                {/*    onChange={handleFilterChange}*/}
                {/*/>*/}
            </div>
<div className="container mx-auto">
            <div className="grid lg:grid-cols-6 md:grid-cols-4 grid-cols-3 gap-4">

                    {filteredColors.map(color => (
                        <div key={color.id}  className="flex flex-col text-xl shadow rounded-md">
                            <div style={{backgroundColor: color.hex_code}} className="min-h-48"></div>
                            <div className="details flex py-3 flex-col">
                                <div className="text-2xl  underline font-semibold">{color.name}</div>

                                {/*<div>Hex: {color.hex_code}</div>*/}
                                <div className="uppercase rounded-sm bg-slate-100 cursor-pointer flex w-full w-auto m-auto px-3 " onClick={() => copyHexToClipboard(color.hex_code)}><span className="pr-3">{color.hex_code}</span><BsCopy className="my-auto"/></div>

                            </div>
                            <div className="buttons bg-slate-100 p-3 flex">
                            <a onClick={() => handleDeleteColor(color.id)}><BsCopy className="my-auto"/></a>
                                {!favoriteColors.includes(color.hex_code) ? (
                                    <button onClick={() => handleSetFavoriteColor(color.hex_code)}>Set Favorite</button>
                                ) : (
                                    <button onClick={() => handleRemoveFavoriteColor(color.hex_code)}>Remove Favorite</button>
                                )}
                            </div>


                        </div>
                    ))}

            </div>


</div>
            <div>
                <h2>Create Color</h2>
                <input
                    type="text"
                    placeholder="Color Name"
                    value={newColorName}
                    onChange={(e) => setNewColorName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Hex Code"
                    value={newColorHex}
                    onChange={(e) => setNewColorHex(e.target.value)}
                />
                <button onClick={handleCreateColor}>Create</button>
            </div>
            {favoriteColors.length > 0 && (
                <div>
                    <h2>Favorite Colors</h2>
                    <div style={{display: 'flex', flexWrap: 'wrap'}}>
                        {favoriteColors.map((hexCode, index) => (
                            <div key={index} style={{
                                backgroundColor: hexCode,
                                width: '50px',
                                height: '50px',
                                marginRight: '10px',
                                marginBottom: '10px'
                            }}>
                                <button onClick={() => handleRemoveFavoriteColor(hexCode)}>Remove</button>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default Color;
